import React, { Component } from "react";
import Header from "./NutritionLabelComponents/Header/Header";
import Nutrients from "./NutritionLabelComponents/Nutrients/Nutrients";
import Vitamins from "./NutritionLabelComponents/Vitamins/Vitamins";
import Styles from "./NutritionLabel.module.css";

class NutritionLabel extends Component {
    state = {
        product: {
            name:"Cheese",
            quantity:100,
            units:"g",
            totalNutrients : {
                ENERC_KCAL:{
                  label : "Energy",
                  quantity : 21814.306200000003,
                  unit : "kcal"
                },
                FAT : {
                  label : "Fat",
                  quantity : 1562.5579472000002,
                  unit : "g"
                },
                FASAT : {
                  label : "Saturated",
                  quantity : 538.5602623999998,
                  unit : "g"
                },
                FATRN : {
                  label : "Trans",
                  quantity : 0.015,
                  unit : "g"
                },
                CHOCDF : {
                  label : "Carbs",
                  quantity : 390.41195,
                  unit : "g"
                },
                FIBTG : {
                  label : "Fiber",
                  quantity : 48.5609,
                  unit : "g"
                },
                SUGAR : {
                  label : "Sugars",
                  quantity : 205.60635,
                  unit : "g"
                },
                PROCNT : {
                  label : "Protein",
                  quantity : 1466.6697507999997,
                  unit : "g"
                },
                CHOLE : {
                  label : "Cholesterol",
                  quantity : 6153.39888,
                  unit : "mg"
                },
                NA : {
                  label : "Sodium",
                  quantity : 14854.027320000001,
                  unit : "mg"
                },
                CA:{
                    label:"Calcium",
                    quantity:10.92,
                    unit:"mg"
                },
                FE:{
                    label:"Iron",
                    quantity:0.2184,
                    unit:"mg"
                },
                VITA_RAE:{
                    label:"Vitamin A",
                    quantity:5.46,
                    unit:"µg"
                },
                VITC:{
                    label:"Vitamin C",
                    quantity:8.372,
                    unit:"mg"
                }
            }
        }
    } 
    render(){
        return(
            <div className={Styles.Container}>
                <Header servingSize={this.props.product ? this.props.product.quantity : 100}/>
                <Nutrients calories={this.props.product.totalNutrients.ENERC_KCAL.quantity}>
                    <ul>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Total Fat</b> {this.props.product.totalNutrients.FAT.quantity ? this.props.product.totalNutrients.FAT.quantity : 0} g
                            <ul>
                                <div className={Styles.Float}>10%</div>
                                <li>Saturated Fat {this.props.product.totalNutrients.FASAT.quantity ? this.props.product.totalNutrients.FASAT.quantity : 0}g</li>
                                <div className={Styles.Float}>10%</div>
                                <li>Trans Fat {this.props.product.totalNutrients.FATRN.quantity ? this.props.product.totalNutrients.FATRN.quantity : 0}</li>
                            </ul>
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Cholesterol</b> {this.props.product.totalNutrients.CHOLE.quantity ? this.props.product.totalNutrients.CHOLE.quantity : 0}mg
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Sodium</b> {this.props.product.totalNutrients.NA.quantity ? this.props.product.totalNutrients.NA.quantity : 0}mg
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Total Carbohydrates</b> {this.props.product.totalNutrients.CHOCDF.quantity ? this.props.product.totalNutrients.CHOCDF.quantity : 0}g
                            <ul>
                                <div className={Styles.Float}>10%</div>
                                <li>Dietary Fiber {this.props.product.totalNutrients.FIBTG.quantity ? this.props.product.totalNutrients.FIBTG.quantity : 0}g</li>
                                <div className={Styles.Float}>10%</div>
                                <li>Sugars {this.props.product.totalNutrients.SUGAR.quantity ? this.props.product.totalNutrients.SUGAR.quantity : 0}g</li>
                            </ul>
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Protein</b> {this.props.product.totalNutrients.PROCNT.quantity ? this.props.product.totalNutrients.PROCNT.quantity : 0}g
                        </li>
                    </ul>
                </Nutrients>
                <Vitamins>
                    <ul>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Vitamin A</b> {this.props.product.totalNutrients.VITA_RAE.quantity ? this.props.product.totalNutrients.VITA_RAE.quantity : 0}mg
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Vitamin C</b> {this.props.product.totalNutrients.VITC.quantity ? this.props.product.totalNutrients.VITC.quantity : 0}mg
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Iron</b> {this.props.product.totalNutrients.FE.quantity ? this.props.product.totalNutrients.FE.quantity : 0}mg
                        </li>
                        <li>
                            <div className={Styles.Float}>10%</div>
                            <b className={Styles.Attribute}>Calcium</b> {this.props.product.totalNutrients.CA.quantity ? this.props.product.totalNutrients.CA.quantity : 0}mg
                        </li>
                    </ul>
                </Vitamins>
            </div>
        )
    }
}

export default NutritionLabel;
